// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title ERC20Permissions - TRUE ERC-7715 with ERC-20 tokens
 * @dev Uses ERC-20 approval pattern - money stays in wallet!
 * 
 * HOW IT WORKS:
 * 1. Player wraps MNT → WMANTLE (ERC-20 token)
 * 2. Player approves this contract to spend WMANTLE (NO MONEY SENT!)
 * 3. Player grants permission with spending cap
 * 4. When weapon vote passes, backend calls executeTeamAction
 * 5. Contract pulls WMANTLE from player's wallet (using approval)
 * 6. Backend receives WMANTLE as payment
 * 
 * BENEFITS:
 * - ✅ Money stays in player's wallet until weapon is used
 * - ✅ One-time approval, no signature per weapon
 * - ✅ Spending cap enforced
 * - ✅ Can revoke anytime
 * - ✅ No refund needed - unspent stays in wallet!
 */
contract ERC20Permissions {
    IERC20 public immutable token; // WMANTLE token
    
    struct Permission {
        uint256 maxAmount;   // Maximum that can be spent
        uint256 spent;       // Amount already spent
        uint256 expiry;      // When permission expires
        bool active;         // Is permission active
    }
    
    // owner => delegate => Permission
    mapping(address => mapping(address => Permission)) public permissions;
    
    // Events
    event PermissionGranted(address indexed owner, address indexed delegate, uint256 maxAmount, uint256 expiry);
    event PermissionIncreased(address indexed owner, address indexed delegate, uint256 newMaxAmount);
    event PermissionRevoked(address indexed owner, address indexed delegate);
    event FundsSpent(address indexed owner, address indexed delegate, uint256 amount);
    event TeamActionExecuted(address indexed leader, uint256 totalAmount, uint256 memberCount);
    
    constructor(address _token) {
        require(_token != address(0), "Invalid token");
        token = IERC20(_token);
    }
    
    /**
     * @dev Grant spending permission (NO MONEY SENT!)
     * Player must first approve this contract to spend their WMANTLE
     * 
     * Steps for player:
     * 1. Wrap MNT to WMANTLE: WMANTLE.deposit{value: 0.1 ether}()
     * 2. Approve contract: WMANTLE.approve(thisContract, 0.1 ether)
     * 3. Grant permission: grantPermission(backend, 0.1 ether, 86400)
     */
    function grantPermission(
        address delegate,
        uint256 maxAmount,
        uint256 duration
    ) external {
        require(delegate != address(0), "Invalid delegate");
        require(maxAmount > 0, "Max amount must be > 0");
        require(duration > 0, "Invalid duration");
        
        // Verify player has approved this contract
        uint256 allowance = token.allowance(msg.sender, address(this));
        require(allowance >= maxAmount, "Insufficient token allowance");
        
        // Verify player has enough balance
        uint256 balance = token.balanceOf(msg.sender);
        require(balance >= maxAmount, "Insufficient token balance");
        
        Permission storage p = permissions[msg.sender][delegate];
        
        if (p.active) {
            // If already active, increase cap
            p.maxAmount += maxAmount;
            p.expiry = block.timestamp + duration;
            emit PermissionIncreased(msg.sender, delegate, p.maxAmount);
        } else {
            // New permission
            p.maxAmount = maxAmount;
            p.spent = 0;
            p.expiry = block.timestamp + duration;
            p.active = true;
            emit PermissionGranted(msg.sender, delegate, maxAmount, block.timestamp + duration);
        }
    }
    
    /**
     * @dev Increase permission cap
     * Player must approve additional amount first
     */
    function increasePermission(address delegate, uint256 additionalAmount) external {
        require(additionalAmount > 0, "Amount must be > 0");
        
        Permission storage p = permissions[msg.sender][delegate];
        require(p.active, "No active permission");
        
        // Verify allowance
        uint256 allowance = token.allowance(msg.sender, address(this));
        uint256 currentAvailable = p.maxAmount - p.spent;
        require(allowance >= currentAvailable + additionalAmount, "Insufficient allowance");
        
        p.maxAmount += additionalAmount;
        
        emit PermissionIncreased(msg.sender, delegate, p.maxAmount);
    }
    
    /**
     * @dev Revoke permission (NO REFUND NEEDED!)
     * Unspent amount stays in player's wallet
     */
    function revokePermission(address delegate) external {
        Permission storage p = permissions[msg.sender][delegate];
        require(p.active, "No active permission");
        
        p.active = false;
        
        emit PermissionRevoked(msg.sender, delegate);
    }
    
    /**
     * @dev Execute team action - PULLS tokens from players' wallets
     * Backend calls this after weapon vote passes
     * 
     * This will:
     * 1. Verify each player has permission
     * 2. Pull WMANTLE from each player's wallet (using their approval)
     * 3. Transfer total to backend as payment
     */
    function executeTeamAction(
        address[] calldata owners,
        uint256[] calldata amounts
    ) external {
        require(owners.length == amounts.length, "Length mismatch");
        require(owners.length > 0, "No owners");
        
        uint256 totalAmount = 0;
        
        // Pull tokens from each player
        for (uint256 i = 0; i < owners.length; i++) {
            address owner = owners[i];
            uint256 amount = amounts[i];
            
            Permission storage p = permissions[owner][msg.sender];
            
            require(p.active, "Permission not active");
            require(block.timestamp < p.expiry, "Permission expired");
            require(p.spent + amount <= p.maxAmount, "Exceeds permission limit");
            
            // PULL tokens from player's wallet
            bool success = token.transferFrom(owner, msg.sender, amount);
            require(success, "Token transfer failed");
            
            // Mark as spent
            p.spent += amount;
            totalAmount += amount;
            
            emit FundsSpent(owner, msg.sender, amount);
        }
        
        emit TeamActionExecuted(msg.sender, totalAmount, owners.length);
    }
    
    /**
     * @dev Get permission details
     */
    function getPermission(address owner, address delegate) external view returns (
        uint256 maxAmount,
        uint256 spent,
        uint256 expiry,
        bool active,
        uint256 available
    ) {
        Permission memory p = permissions[owner][delegate];
        bool isActive = p.active && block.timestamp < p.expiry;
        uint256 availableAmount = isActive ? (p.maxAmount - p.spent) : 0;
        
        return (p.maxAmount, p.spent, p.expiry, isActive, availableAmount);
    }
    
    /**
     * @dev Get available spending amount
     */
    function getAvailableAmount(address owner, address delegate) external view returns (uint256) {
        Permission memory p = permissions[owner][delegate];
        
        if (!p.active || block.timestamp >= p.expiry) {
            return 0;
        }
        
        return p.maxAmount - p.spent;
    }
    
    /**
     * @dev Get total pool for team
     */
    function getTotalPool(address leader, address[] calldata members) external view returns (uint256) {
        uint256 total = 0;
        
        for (uint256 i = 0; i < members.length; i++) {
            Permission memory p = permissions[members[i]][leader];
            
            if (p.active && block.timestamp < p.expiry) {
                total += (p.maxAmount - p.spent);
            }
        }
        
        return total;
    }
}

contracts/PracticalERC7715.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title PracticalERC7715 - Best of Both Worlds
 * @dev Practical ERC-7715 implementation for gaming
 * 
 * HOW IT WORKS:
 * 1. Player grants permission with spending cap (e.g., 0.1 MNT max)
 * 2. Player sends ONLY the cap amount ONCE to contract as collateral
 * 3. Backend can spend from this collateral up to the cap
 * 4. When permission is revoked, unspent amount is refunded
 * 5. Player can increase cap by sending more
 * 
 * BENEFITS:
 * - ✅ One-time approval (not per weapon)
 * - ✅ Spending cap enforced
 * - ✅ Gasless weapon launches for players
 * - ✅ Refund of unspent amount
 * - ✅ Can increase cap anytime
 * - ✅ Backend pays gas, gets weapon cost
 */
contract PracticalERC7715 {
    struct Permission {
        uint256 maxAmount;   // Maximum that can be spent (collateral deposited)
        uint256 spent;       // Amount already spent
        uint256 expiry;      // When permission expires
        bool active;         // Is permission active
    }
    
    // owner => delegate => Permission
    mapping(address => mapping(address => Permission)) public permissions;
    
    // Events
    event PermissionGranted(address indexed owner, address indexed delegate, uint256 maxAmount, uint256 expiry);
    event PermissionIncreased(address indexed owner, address indexed delegate, uint256 newMaxAmount);
    event PermissionRevoked(address indexed owner, address indexed delegate, uint256 refundAmount);
    event FundsSpent(address indexed owner, address indexed delegate, uint256 amount);
    event TeamActionExecuted(address indexed leader, uint256 totalAmount, uint256 memberCount);
    
    /**
     * @dev Grant permission by depositing collateral
     * Player sends MNT once, backend can spend from it
     */
    function grantPermission(
        address delegate,
        uint256 duration
    ) external payable {
        require(delegate != address(0), "Invalid delegate");
        require(msg.value > 0, "Must deposit collateral");
        require(duration > 0, "Invalid duration");
        
        Permission storage p = permissions[msg.sender][delegate];
        
        if (p.active) {
            // If already active, add to existing
            p.maxAmount += msg.value;
            p.expiry = block.timestamp + duration; // Reset expiry
            emit PermissionIncreased(msg.sender, delegate, p.maxAmount);
        } else {
            // New permission
            p.maxAmount = msg.value;
            p.spent = 0;
            p.expiry = block.timestamp + duration;
            p.active = true;
            emit PermissionGranted(msg.sender, delegate, msg.value, block.timestamp + duration);
        }
    }
    
    /**
     * @dev Increase permission by adding more collateral
     */
    function increasePermission(address delegate) external payable {
        require(msg.value > 0, "Must send MNT");
        
        Permission storage p = permissions[msg.sender][delegate];
        require(p.active, "No active permission");
        
        p.maxAmount += msg.value;
        
        emit PermissionIncreased(msg.sender, delegate, p.maxAmount);
    }
    
    /**
     * @dev Revoke permission and get refund of unspent collateral
     */
    function revokePermission(address delegate) external {
        Permission storage p = permissions[msg.sender][delegate];
        require(p.active, "No active permission");
        
        uint256 refundAmount = p.maxAmount - p.spent;
        p.active = false;
        
        if (refundAmount > 0) {
            payable(msg.sender).transfer(refundAmount);
        }
        
        emit PermissionRevoked(msg.sender, delegate, refundAmount);
    }
    
    /**
     * @dev Backend executes team action (weapon launch)
     * Spends from each player's collateral
     */
    function executeTeamAction(
        address[] calldata owners,
        uint256[] calldata amounts
    ) external {
        require(owners.length == amounts.length, "Length mismatch");
        require(owners.length > 0, "No owners");
        
        uint256 totalAmount = 0;
        
        // Verify permissions and mark as spent
        for (uint256 i = 0; i < owners.length; i++) {
            address owner = owners[i];
            uint256 amount = amounts[i];
            
            Permission storage p = permissions[owner][msg.sender];
            
            require(p.active, "Permission not active");
            require(block.timestamp < p.expiry, "Permission expired");
            require(p.spent + amount <= p.maxAmount, "Exceeds permission limit");
            
            // Debit from this player's collateral
            p.spent += amount;
            totalAmount += amount;
            
            emit FundsSpent(owner, msg.sender, amount);
        }
        
        // Transfer total to backend (payment for gas + service)
        payable(msg.sender).transfer(totalAmount);
        
        emit TeamActionExecuted(msg.sender, totalAmount, owners.length);
    }
    
    /**
     * @dev Get permission details
     */
    function getPermission(address owner, address delegate) external view returns (
        uint256 maxAmount,
        uint256 spent,
        uint256 expiry,
        bool active,
        uint256 available
    ) {
        Permission memory p = permissions[owner][delegate];
        bool isActive = p.active && block.timestamp < p.expiry;
        uint256 availableAmount = isActive ? (p.maxAmount - p.spent) : 0;
        
        return (p.maxAmount, p.spent, p.expiry, isActive, availableAmount);
    }
    
    /**
     * @dev Get available amount for spending
     */
    function getAvailableAmount(address owner, address delegate) external view returns (uint256) {
        Permission memory p = permissions[owner][delegate];
        
        if (!p.active || block.timestamp >= p.expiry) {
            return 0;
        }
        
        return p.maxAmount - p.spent;
    }
    
    /**
     * @dev Get total pool available for team
     */
    function getTotalPool(address leader, address[] calldata members) external view returns (uint256) {
        uint256 total = 0;
        
        for (uint256 i = 0; i < members.length; i++) {
            Permission memory p = permissions[members[i]][leader];
            
            if (p.active && block.timestamp < p.expiry) {
                total += (p.maxAmount - p.spent);
            }
        }
        
        return total;
    }
}

contracts/TrueERC7715Permissions.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title TrueERC7715Permissions
 * @dev TRUE ERC-7715 Implementation
 * 
 * HOW IT WORKS:
 * 1. Player grants permission (sets spending cap) - NO PAYMENT
 * 2. Funds stay in player's wallet
 * 3. When weapon is used:
 *    - Backend creates transaction
 *    - Each player MUST approve and send their share
 *    - Backend collects all shares and executes
 * 4. Backend pays gas, gets weapon cost as payment
 * 
 * This requires MULTI-SIG style execution where each player
 * must approve their contribution for each weapon launch
 */
contract TrueERC7715Permissions {
    struct Permission {
        uint256 maxAmount;   // Maximum total that can be spent
        uint256 spent;       // Amount already spent
        uint256 expiry;      // When permission expires
        bool active;         // Is permission active
    }
    
    struct PendingAction {
        address[] owners;           // Players contributing
        uint256[] amounts;          // Amount from each player
        mapping(address => bool) approved;  // Who has approved
        uint256 approvalCount;      // How many approved
        uint256 totalAmount;        // Total needed
        bool executed;              // Has been executed
        uint256 createdAt;          // When created
    }
    
    // owner => delegate => Permission
    mapping(address => mapping(address => Permission)) public permissions;
    
    // actionId => PendingAction
    mapping(bytes32 => PendingAction) public pendingActions;
    
    // Events
    event PermissionGranted(address indexed owner, address indexed delegate, uint256 maxAmount, uint256 expiry);
    event PermissionRevoked(address indexed owner, address indexed delegate);
    event ActionProposed(bytes32 indexed actionId, address indexed proposer, uint256 totalAmount);
    event ActionApproved(bytes32 indexed actionId, address indexed approver, uint256 amount);
    event ActionExecuted(bytes32 indexed actionId, uint256 totalAmount);
    event FundsSpent(address indexed owner, address indexed delegate, uint256 amount);
    
    /**
     * @dev Grant spending permission (NO PAYMENT!)
     */
    function grantPermission(
        address delegate,
        uint256 maxAmount,
        uint256 duration
    ) external {
        require(delegate != address(0), "Invalid delegate");
        require(maxAmount > 0, "Max amount must be > 0");
        require(duration > 0, "Invalid duration");
        
        permissions[msg.sender][delegate] = Permission({
            maxAmount: maxAmount,
            spent: 0,
            expiry: block.timestamp + duration,
            active: true
        });
        
        emit PermissionGranted(msg.sender, delegate, maxAmount, block.timestamp + duration);
    }
    
    /**
     * @dev Revoke permission
     */
    function revokePermission(address delegate) external {
        Permission storage p = permissions[msg.sender][delegate];
        require(p.active, "No active permission");
        
        p.active = false;
        
        emit PermissionRevoked(msg.sender, delegate);
    }
    
    /**
     * @dev Propose a team action (weapon launch)
     * Backend calls this to initiate weapon purchase
     */
    function proposeTeamAction(
        address[] calldata owners,
        uint256[] calldata amounts
    ) external returns (bytes32 actionId) {
        require(owners.length == amounts.length, "Length mismatch");
        require(owners.length > 0, "No owners");
        
        // Generate unique action ID
        actionId = keccak256(abi.encodePacked(
            block.timestamp,
            msg.sender,
            owners,
            amounts
        ));
        
        PendingAction storage action = pendingActions[actionId];
        require(action.createdAt == 0, "Action already exists");
        
        uint256 total = 0;
        for (uint256 i = 0; i < amounts.length; i++) {
            total += amounts[i];
        }
        
        action.owners = owners;
        action.amounts = amounts;
        action.totalAmount = total;
        action.createdAt = block.timestamp;
        action.executed = false;
        action.approvalCount = 0;
        
        emit ActionProposed(actionId, msg.sender, total);
        
        return actionId;
    }
    
    /**
     * @dev Player approves and sends their share for a weapon
     * Each player must call this and send their MNT
     */
    function approveAndPay(bytes32 actionId, address delegate) external payable {
        PendingAction storage action = pendingActions[actionId];
        require(action.createdAt > 0, "Action not found");
        require(!action.executed, "Already executed");
        require(!action.approved[msg.sender], "Already approved");
        require(block.timestamp < action.createdAt + 5 minutes, "Action expired");
        
        // Find this player's amount
        uint256 playerAmount = 0;
        bool found = false;
        for (uint256 i = 0; i < action.owners.length; i++) {
            if (action.owners[i] == msg.sender) {
                playerAmount = action.amounts[i];
                found = true;
                break;
            }
        }
        
        require(found, "Not part of this action");
        require(msg.value == playerAmount, "Must send exact amount");
        
        // Check permission
        Permission storage p = permissions[msg.sender][delegate];
        require(p.active, "Permission not active");
        require(block.timestamp < p.expiry, "Permission expired");
        require(p.spent + playerAmount <= p.maxAmount, "Exceeds permission");
        
        // Mark as approved and spent
        action.approved[msg.sender] = true;
        action.approvalCount++;
        p.spent += playerAmount;
        
        emit ActionApproved(actionId, msg.sender, playerAmount);
        emit FundsSpent(msg.sender, delegate, playerAmount);
        
        // If all approved, execute automatically
        if (action.approvalCount == action.owners.length) {
            _executeAction(actionId, delegate);
        }
    }
    
    /**
     * @dev Execute action after all approvals (internal)
     */
    function _executeAction(bytes32 actionId, address delegate) internal {
        PendingAction storage action = pendingActions[actionId];
        require(!action.executed, "Already executed");
        require(action.approvalCount == action.owners.length, "Not all approved");
        
        action.executed = true;
        
        // Transfer all collected funds to delegate (backend)
        payable(delegate).transfer(action.totalAmount);
        
        emit ActionExecuted(actionId, action.totalAmount);
    }
    
    /**
     * @dev Get permission details
     */
    function getPermission(address owner, address delegate) external view returns (
        uint256 maxAmount,
        uint256 spent,
        uint256 expiry,
        bool active,
        uint256 available
    ) {
        Permission memory p = permissions[owner][delegate];
        bool isActive = p.active && block.timestamp < p.expiry;
        uint256 availableAmount = isActive ? (p.maxAmount - p.spent) : 0;
        
        return (p.maxAmount, p.spent, p.expiry, isActive, availableAmount);
    }
    
    /**
     * @dev Check if action is fully approved
     */
    function isActionReady(bytes32 actionId) external view returns (bool) {
        PendingAction storage action = pendingActions[actionId];
        return action.approvalCount == action.owners.length && !action.executed;
    }
}

contracts/WMANTLE.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title WMANTLE - Wrapped Mantle
 * @dev Wrap native MNT into ERC-20 token
 * This allows ERC-20 approval pattern for spending permissions
 */
contract WMANTLE is ERC20 {
    event Deposit(address indexed account, uint256 amount);
    event Withdrawal(address indexed account, uint256 amount);
    
    constructor() ERC20("Wrapped Mantle", "WMANTLE") {}
    
    /**
     * @dev Wrap MNT to WMANTLE
     */
    function deposit() external payable {
        require(msg.value > 0, "Must send MNT");
        _mint(msg.sender, msg.value);
        emit Deposit(msg.sender, msg.value);
    }
    
    /**
     * @dev Unwrap WMANTLE to MNT
     */
    function withdraw(uint256 amount) external {
        require(balanceOf(msg.sender) >= amount, "Insufficient balance");
        _burn(msg.sender, amount);
        payable(msg.sender).transfer(amount);
        emit Withdrawal(msg.sender, amount);
    }
    
    /**
     * @dev Allow depositing via receive
     */
    receive() external payable {
        _mint(msg.sender, msg.value);
        emit Deposit(msg.sender, msg.value);
    }
}

